mapper.insert:
/* qnaInsert */
insert into qna (qna_no, qna_writedate, qna_title, qna_content, qna_category, qna_file1, qna_modifydate, mem_no, prd_d_no) values 
(qna_seq.nextval, sysdate, #{qna_title}, #{qna_content}, #{qna_category}, #{qna_file1}, sysdate, #{mem_no} ,#{prd_d_no})

mapper.select:
/* qna qnaList */
select qna_no, mem_name, qna_title, qna_category, qna_hit, prd_d_no, to_char(qna_writedate, 'YYYY-MM-DD HH24:MI:SS') as qna_writedate from 
(select list.*, rownum as rnum from (


	order by qna_no
	order by qna_writedate


	ASC
	DESC

) list
)

	
		
			rnum between #{start_row} and #{end_row}
