mapper.select:
/* qna qnaList */
select review_no, mem_name, review_title, review_score, review_hit, prd_d_no, to_char(review_writedate, 'YYYY-MM-DD HH24:MI:SS') as review_writedate from 
(select list.*, rownum as rnum from (


	order by review_no
	order by review_writedate


	ASC
	DESC

) list
)

	
		
			rnum between #{start_row} and #{end_row}
